// 通过这个函数，你可以验证你实现的SIMD哈希函数的正确性
int main()
{
    bit32 state[4][4];
    array<std::string,4> input = {
      "",
      "",
      "",
      ""
    };
    MD5Hash_SIMD(input, state);
    for(int i1=0; i1<4; i1++)
    {
      for (int i = 0; i < 4; i += 1)
      {
          cout << std::setw(8) << std::setfill('0') << hex << state[i1][i];
      }
      cout << endl;
    }
    cout << endl;
}